var i = 0;
var txt = 'This is my current WIP main project thatll be combined within FireBase. It will eventually have a register and login like other websites. If logged in with the correct details, then it will show information. If the details are wrong, it will say "You are not welcome here". Other things are currently being added as well.'; /* The text */
var speed = 50; /* The speed/duration of the effect in milliseconds */


function typeWriter() {
  if (i < txt.length) {
    document.getElementById("kitty").innerHTML += txt.charAt(i);
    i++;
    setTimeout(typeWriter, speed); 
} else {
    i = 0;
    document.getElementById("kitty").innerHTML = "";
    typeWriter();
}
}

typeWriter(); 